var modal = document.getElementById("myModal");
var modal2 = document.getElementById("myModal2");
var modal3 = document.getElementById("myModal3");
var modal4 = document.getElementById("myModal4");
        
var img = document.getElementById("myImg");
var img2 = document.getElementById("myImg2");
var img3 = document.getElementById("myImg3");
var img4 = document.getElementById("myImg4");

var modalImg = document.getElementById("img01");
var chgImg = document.getElementById("img02");

var modalImg2 = document.getElementById("img03");
var chgImg2 = document.getElementById("img04");

var modalImg3 = document.getElementById("img05");
var chgImg3 = document.getElementById("img06");

var modalImg4 = document.getElementById("img07");
var chgImg4 = document.getElementById("img08");

img.onclick = function(){
    modal.style.display = "block";
    modalImg.src = chgImg.src;
}

img2.onclick = function(){
    modal2.style.display = "block";
    modalImg2.src = chgImg2.src;
}

img3.onclick = function(){
    modal3.style.display = "block";
    modalImg3.src = chgImg3.src;
}

img4.onclick = function(){
    modal4.style.display = "block";
    modalImg4.src = chgImg4.src;
}

window.onclick = function() { 
    if (event.target == modal) {
        modal.style.display = "none";
    } else if (event.target == modal2) {
        modal2.style.display = "none";
    } else if (event.target == modal3) {
        modal3.style.display = "none";
    } else if (event.target == modal4) {
        modal4.style.display = "none";
    } else {
        
    }
}